<?php  namespace BtcRelax;	use \Exception;	/**	 * Exception which causes in context of known user (Session internal error).	 */	class SessionExpiredException extends Exception {			public function __construct($sessionId = null) {    			if  (isset($sessionId))                            {                                error_log('Session Id:' . $sessionId . ' was expired!');                                session_destroy();                               }    			}	}?>