<?php   namespace BtcRelax;    use \BtcRelax\Utils;    use \BtcRelax\Config;    use \BtcRelax\AM;     global $core;    $registerResult = null;        $status = $core->getSessionState();            if ($status == SecureSession::STATUS_GUEST)            {                $action = Utils::tryGetParam('action');                if ($action != false )                {                    if ($action === 'createUser')                    {                        $registerResult = false;                        $v_bitId = $core->getBitId();                        if ($v_bitId)                        {                            $am = $core->GetAM();                            $res = $am->CreateNewUser(null, $v_bitId);                            if ($res)                            {                                $core->setBitId($v_bitId);                                $registerResult = true;                            };                        };                        //                        //                        //                        if ($core->TryToCreateUser())//                        {//                          $registerResult = true;  //                        }//                        else//                        {//                          $registerResult = false;  //                        };                    }                }            }            else            {                       Utils::Redirect('main');            }?>