-- -----------------------------------------------------
-- View `vwCustomerBitid`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `vwCustomerBitid` ;
SHOW WARNINGS;
DROP TABLE IF EXISTS `vwCustomerBitid`;
SHOW WARNINGS;
CREATE  OR REPLACE ALGORITHM=UNDEFINED DEFINER=`ident`@`%` SQL SECURITY DEFINER VIEW `vwCustomerBitid` AS select `Customers`.`idCustomer` AS `idCustomer`,`Idetities`.`IdentityKey` AS `BitId` from (`Customers` join `Idetities` on(((`Idetities`.`IdentTypeCode` = 'bitid') and (`Idetities`.`idCustomer` = `Customers`.`idCustomer`))));
SHOW WARNINGS;

-- -----------------------------------------------------
-- View `vwOrders`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `vwOrders` ;
SHOW WARNINGS;
DROP TABLE IF EXISTS `vwOrders`;
SHOW WARNINGS;
CREATE  OR REPLACE VIEW vwOrders AS
    SELECT 
        idOrder,
        CreateDate,
        EndDate,
        OrderState,
        BTCPrice,
        PricingDate,
        InvoiceAddress,
        idCreator,
        DeliveryMethod,
        InvoiceBalance,
        BalanceDate,
        GETHASHBYORDERID(idOrder) AS OrderHash
    FROM
        Orders;
SHOW WARNINGS;

-- -----------------------------------------------------
-- View `vwBookmarks`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `vwBookmarks` ;
SHOW WARNINGS;
DROP TABLE IF EXISTS `vwBookmarks`;
SHOW WARNINGS;
CREATE  OR REPLACE VIEW `vwBookmarks` AS
SELECT `Bookmarks`.`idBookmark`,
    `Bookmarks`.`CreateDate`,
    `Bookmarks`.`idOrder`,
    `Bookmarks`.`Quantity`,
    `Bookmarks`.`EndDate`,
    `Bookmarks`.`Latitude`,
    `Bookmarks`.`Longitude`,
    `Bookmarks`.`Link`,
    `Bookmarks`.`Description`,
    `Bookmarks`.`RegionTitle`,
    `Bookmarks`.`CustomPrice`,
    `Bookmarks`.`PriceCurrency`,
    `Bookmarks`.`AdvertiseTitle`,
    `Bookmarks`.`UnlockDate`,
    `Bookmarks`.`State`,
    `Bookmarks`.`IdDroper`,
    `Bookmarks`.`TargetAddress`,
     GetHashByBookmarkId(idBookmark) as BookmarkHash
FROM `Bookmarks`;
SHOW WARNINGS;

